mapper.select:
SELECT * FROM ps_order 
		WHERE buyer_id LIKE #{isSearch}
		ORDER BY order_num DESC

SELECT * FROM ps_order
WHERE status LIKE #{isSearch}
ORDER BY order_num DESC

SELECT * FROM ps_order
WHERE delivery_company LIKE #{isSearch}
ORDER BY order_num DESC

select * from ps_orderlist a, ps_product b, ps_order c where a.seller_id = #{sessionId} and a.order_num = #{ORDER_NUM} and a.product_num = b.product_num and a.order_num = c.order_num and
c.status != '5'
c.status = #{status}

select b.PRICE, b.SUBJECT, b.FIRST_IMG, b.category_num, b.product_num, a.ORDER_NUM, a.ORDER_QUANTITY from(select * from PS_ORDERLIST)a, ps_product b, ps_order c where a.ORDER_NUM = #{ORDER_NUM} and a.product_num = b.product_num and c.order_num = a.order_num and
c.status != '5'
c.status = #{status}

select * from ps_alarm WHERE ALARM_TARGET = #{sessionId}





and ALARM_CHECK = #{ALARM_CHECK} 



and ALARM_CHECK = #{ALARM_CHECK} 


order by ALARM_REGDATE desc

mapper.update:
update ps_order set
step3_date = sysdate,
STATUS = '2',
DELIVERY_COMPANY = #{DELIVERY_COMPANY},
INVOICE_NUM = #{INVOICE_NUM}
where ORDER_NUM = #{ORDER_NUM}
